package expDeepdive0826;

public class Manager extends Employee{
	private int numSubordinates;

	public Manager(String name, double salary, String position, int numSubordinates) {
		super(name, salary, position);
		this.numSubordinates = numSubordinates;
	}

	@Override
	public void increaseSalary(double percentage) {
		// TODO Auto-generated method stub
		super.increaseSalary(percentage);
	}

	@Override
	public String toString() {
		return "Manager [numSubordinates=" + numSubordinates + "]";
	}
	
	
	
}
